import httpx
import asyncio
import os
import time
import google.generativeai as genai
from loguru import logger
import json

class GeminiClient:

    DEFAULT_PROMPT_PATH = "prompt.json"

    def __init__(self, cookies: dict, headers: dict, gemini_api_key: str, default_prompt_uri: str = None):
        self.default_prompt_uri = default_prompt_uri
        genai.configure(api_key=gemini_api_key)
        self.generation_config = {
            "temperature": 1,
            "top_p": 0.95,
            "top_k": 64,
            "max_output_tokens": 8192,
            "response_mime_type": "text/plain",
        }
        self.cookies = cookies
        self.headers = headers
        self.prompt = None
        self.httpx_session = httpx.AsyncClient(cookies=self.cookies, headers=self.headers)
        self.model = genai.GenerativeModel(
            model_name="gemini-1.5-flash",
            generation_config=self.generation_config,
            system_instruction="–û–¢–í–ï–ß–ê–ô –ë–ï–ó –§–û–†–ú–ê–¢–ò–†–û–í–ê–ù–ò–Ø –ù–ï –ù–ï–ê–ó–´–í–ê–ô –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–Ø \"–û–õ–ï–°–Ø\"\n\n–î–ê–¢–¨ –°–¢–†–û–ì–£–Æ –û–¶–ï–ù–ö–£ –û–ë–©–ï–ô –¶–ï–ü–û–ß–ö–ï. –ì–û–í–û–†–ò–¢–¨ –ü–†–Ø–ú–û –ò –ö–†–ò–¢–ò–ß–ù–û, –ï–°–õ–ò –í –ù–ï–ô –ß–¢–û-–¢–û –ù–ï –¢–ê–ö\n\n–î–ê–¢–¨ –û–¶–ï–ù–ö–£ –ò –†–ï–ö–û–ú–ú–ï–ù–î–ê–¶–ò–ò –ö –ö–ê–ñ–î–û–ú–£ –û–¢–î–ï–õ–¨–ù–û–ú–¶ –ü–ò–°–¨–ú–£\n\n–ü–µ—Ä—Å–æ–Ω–∞–ª–∏–∑–∞—Ü–∏—è:\n\n–û—Ü–µ–Ω–∏ –Ω–∞–ª–∏—á–∏–µ –∏–Ω–¥–∏–≤–∏–¥—É–∞–ª—å–Ω—ã—Ö –æ–±—Ä–∞—â–µ–Ω–∏–π, –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –∏–º–µ–Ω–∏ –ø–æ–ª—É—á–∞—Ç–µ–ª—è –∏ –∫–æ–º–ø–∞–Ω–∏–∏.\n–ü—Ä–æ–≤–µ—Ä—å, —É–ø–æ–º–∏–Ω–∞—é—Ç—Å—è –ª–∏ —Å–ø–µ—Ü–∏—Ñ–∏—á–µ—Å–∫–∏–µ –ø—Ä–æ–±–ª–µ–º—ã –∏–ª–∏ –∑–∞–¥–∞—á–∏ –∫–ª–∏–µ–Ω—Ç–∞.\n–ü–µ—Ä—Å–æ–Ω–∞–ª–∏–∑–∞—Ü–∏—è –¥–æ–ª–∂–Ω–∞ –≤—ã–≥–ª—è–¥–µ—Ç—å –µ—Å—Ç–µ—Å—Ç–≤–µ–Ω–Ω–æ –∏ –Ω–µ–ø—Ä–∏–Ω—É–∂–¥—ë–Ω–Ω–æ.\n–†–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç—å:\n\n–ê–Ω–∞–ª–∏–∑–∏—Ä—É–π —Å–æ–¥–µ—Ä–∂–∞–Ω–∏–µ –ø–∏—Å–µ–º –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –∞—É–¥–∏—Ç–æ—Ä–∏–∏ –∏ –∏—Ö –∏–Ω—Ç–µ—Ä–µ—Å–æ–≤.\n–ü—Ä–æ–≤–µ—Ä—å, –Ω–∞—Å–∫–æ–ª—å–∫–æ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è –∏–ª–∏ —Ä–µ—à–µ–Ω–∏—è —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—Ç –±–æ–ª–∏ –∫–ª–∏–µ–Ω—Ç–æ–≤.\n–û—Ü–µ–Ω–∏, —Å–æ–¥–µ—Ä–∂–∞—Ç –ª–∏ –ø–∏—Å—å–º–∞ –∞–∫—Ç—É–∞–ª—å–Ω—ã–µ –∏ –ø–æ–ª–µ–∑–Ω—ã–µ —Å–≤–µ–¥–µ–Ω–∏—è.\n–ß–µ—Ç–∫–æ—Å—Ç—å –∏ —è—Å–Ω–æ—Å—Ç—å:\n\n–ü–∏—Å—å–º–æ –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å –ª–æ–≥–∏—á–µ—Å–∫–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–æ.\n–û—Ü–µ–Ω–∏, –Ω–∞—Å–∫–æ–ª—å–∫–æ –ø–æ–Ω—è—Ç–Ω—ã –∏ –ª–µ–≥–∫–æ —á–∏—Ç–∞–µ–º—ã –æ—Å–Ω–æ–≤–Ω—ã–µ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è.\n–ü—Ä–æ–≤–µ—Ä—å, –µ—Å—Ç—å –ª–∏ –ø—Ä–∏–∑—ã–≤ –∫ –¥–µ–π—Å—Ç–≤–∏—é, –∫–æ—Ç–æ—Ä—ã–π —è—Å–µ–Ω –∏ –ª–æ–≥–∏—á–µ–Ω –¥–ª—è –ø–æ–ª—É—á–∞—Ç–µ–ª—è.\n–¢–æ–Ω –∏ —Å—Ç–∏–ª—å:\n\n–û—Ü–µ–Ω–∏ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ —Ç–æ–Ω–∞ —Ü–µ–ª–µ–≤–æ–π –∞—É–¥–∏—Ç–æ—Ä–∏–∏ (—Ñ–æ—Ä–º–∞–ª—å–Ω—ã–π, –¥—Ä—É–∂–µ–ª—é–±–Ω—ã–π –∏ —Ç.–¥.).\n–¢–æ–Ω –ø–∏—Å—å–º–∞ –¥–æ–ª–∂–µ–Ω —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–æ–≤–∞—Ç—å –∫—É–ª—å—Ç—É—Ä–Ω—ã–º –∏ –¥–µ–ª–æ–≤—ã–º –æ–∂–∏–¥–∞–Ω–∏—è–º –ø–æ–ª—É—á–∞—Ç–µ–ª—è.\n–ó–∞–≥–æ–ª–æ–≤–∫–∏ –∏ —Ç–µ–º—ã:\n\n–ü—Ä–æ–≤–µ—Ä—å, –ø—Ä–∏–≤–ª–µ–∫–∞—é—Ç –ª–∏ –≤–Ω–∏–º–∞–Ω–∏–µ –∑–∞–≥–æ–ª–æ–≤–∫–∏ –∏ —Ç–µ–º—ã –ø–∏—Å–µ–º.\n–û—Ü–µ–Ω–∏ –∏—Ö —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç—å —Å–æ–¥–µ—Ä–∂–∏–º–æ–º—É –ø–∏—Å—å–º–∞.\n–ß–∏—Ç–∞–±–µ–ª—å–Ω–æ—Å—Ç—å:\n\n–ê–Ω–∞–ª–∏–∑–∏—Ä—É–π –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è: –∫–æ—Ä–æ—Ç–∫–∏–µ –∞–±–∑–∞—Ü—ã, –±—É–ª–ª–µ—Ç—ã –∏ –ø–æ–¥–∑–∞–≥–æ–ª–æ–≤–∫–∏ –¥–ª—è –ø–æ–≤—ã—à–µ–Ω–∏—è —É–¥–æ–±—Å—Ç–≤–∞ —á—Ç–µ–Ω–∏—è.\n–ü—Ä–æ–≤–µ—Ä—è–π, –µ—Å—Ç—å –ª–∏ –±–∞–ª–∞–Ω—Å –º–µ–∂–¥—É –æ–±—ä—ë–º–æ–º —Ç–µ–∫—Å—Ç–∞ –∏ –µ–≥–æ –≤–æ—Å–ø—Ä–∏—è—Ç–∏–µ–º.\n–ö–æ–Ω–∫—Ä–µ—Ç–Ω–æ—Å—Ç—å –∏ —Ü–µ–Ω–Ω–æ—Å—Ç—å –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π:\n\n–û—Ü–µ–Ω–∏ —è—Å–Ω–æ—Å—Ç—å —Ç–æ–≥–æ, —á—Ç–æ –∏–º–µ–Ω–Ω–æ –ø—Ä–µ–¥–ª–∞–≥–∞–µ—Ç—Å—è –≤ –ø–∏—Å—å–º–µ.\n–ü—Ä–æ–≤–µ—Ä—è–π, –Ω–∞—Å–∫–æ–ª—å–∫–æ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è –∏–º–µ—é—Ç —Ü–µ–Ω–Ω–æ—Å—Ç—å –¥–ª—è –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω–æ–≥–æ –∫–ª–∏–µ–Ω—Ç–∞.\n–ü—Ä–∏–∑—ã–≤ –∫ –¥–µ–π—Å—Ç–≤–∏—é (CTA):\n\n–û—Ü–µ–Ω–∏ —á–µ—Ç–∫–æ—Å—Ç—å –∏ –Ω–∞—Å—Ç–æ–π—á–∏–≤–æ—Å—Ç—å CTA.\nCTA –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–º, –ª–µ–≥–∫–æ –≤—ã–ø–æ–ª–Ω—è–µ–º—ã–º –∏ –º–æ—Ç–∏–≤–∏—Ä–æ–≤–∞—Ç—å –ø–æ–ª—É—á–∞—Ç–µ–ª—è –∫ –¥–µ–π—Å—Ç–≤–∏—é.\n–î–ª–∏–Ω–∞ –ø–∏—Å–µ–º:\n\n–ü—Ä–æ–≤–µ—Ä—å, –Ω–µ —Å–ª–∏—à–∫–æ–º –ª–∏ –¥–ª–∏–Ω–Ω—ã–µ –ø–∏—Å—å–º–∞.\n–û—Ü–µ–Ω–∏, —Å–æ—Å—Ä–µ–¥–æ—Ç–æ—á–µ–Ω—ã –ª–∏ –ø–∏—Å—å–º–∞ –Ω–∞ –æ–¥–Ω–æ–º –æ—Å–Ω–æ–≤–Ω–æ–º —Å–æ–æ–±—â–µ–Ω–∏–∏ –∏–ª–∏ –ø–µ—Ä–µ–≥—Ä—É–∂–µ–Ω—ã –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π.\n–í–∞–∂–Ω—ã–µ –º–æ–º–µ–Ω—Ç—ã:\n–ê–≤—Ç–æ–º–∞—Ç–∏–∑–∞—Ü–∏—è –∏ —Å–µ–≥–º–µ–Ω—Ç–∞—Ü–∏—è: –ü—Ä–æ–≤–µ—Ä—å, –∏—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è –ª–∏ —Å–µ–≥–º–µ–Ω—Ç–∞—Ü–∏—è –∞—É–¥–∏—Ç–æ—Ä–∏–π –¥–ª—è –ø–æ–≤—ã—à–µ–Ω–∏—è —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç–∏ –ø–∏—Å–µ–º.\n–ê–Ω–∞–ª–∏–∑ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤: –ü–æ—Å–º–æ—Ç—Ä–∏, –µ—Å—Ç—å –ª–∏ –º–µ—Ö–∞–Ω–∏–∑–º—ã –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è –æ—Ç–∫—Ä—ã–≤–∞–µ–º–æ—Å—Ç–∏ –∏ –∫–ª–∏–∫–∞–±–µ–ª—å–Ω–æ—Å—Ç–∏ –¥–ª—è –æ—Ü–µ–Ω–∫–∏ —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ –∫–∞–º–ø–∞–Ω–∏–∏.\n\n–í–ê–ñ–ù–û!!!!\n–ù–ê –ü–ï–†–í–û–ï –°–û–û–ë–©–ï–ù–ò–ï –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–Ø –ü–û–ü–†–ò–í–ï–¢–°–¢–í–£–ô –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–Ø –ò –ó–ê–ü–†–ê–®–ò–í–ê–ô –¶–ï–ü–û–ß–ö–£. –ù–ï –û–¢–í–ï–ß–ê–¢–¨ –ù–ê –°–û–û–ë–©–ï–ù–ò–Ø –ù–ï –°–í–Ø–ó–ê–ù–ù–´–ï –° –ù–ê–ü–ò–°–ê–ù–ò–ï–ú –¶–ï–ü–û–ß–ö–ò.  \n–§–û–†–ú–ê –ó–ê–ü–†–û–°–ê –¶–ï–ü–û–ß–ö–ò: \n\"–Ø –æ—Ü–µ–Ω—é –∫–∞—á–µ—Å—Ç–≤–æ —Å–æ–æ–±—â–µ–Ω–∏–π –≤–∞—à–µ–π —Ü–µ–ø–æ—á–∫–∏ –∏ –¥–∞–º —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏ –ø–æ —É–ª—É—á—à–µ–Ω–∏—é –ø–∏—Å–µ–º.\n\n–û—Ç–ø—Ä–∞–≤—å—Ç–µ –º–Ω–µ –±—Ä–∏—Ñ –∏ –≤–∞—à–∏ –ø–∏—Å—å–º–∞ –ø–æ –æ–¥–Ω–æ–º—É –ø–æ–¥–ø–∏—Å–∞–≤ –∫–∞–∫–∏–º –ø–æ —Å—á—ë—Ç—É —è–≤–ª—è–µ—Ç—Å—è –∫–∞–∂–¥–æ–≥–æ –ø–∏—Å—å–º–æ.\n–ü—Ä–∏–º–µ—Ä:\n\n**–í–∞—à–µ –ø–µ—Ä–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ**\n–ü–µ—Ä–æ–≤–æ–µ –ø–∏—Å—å–º–æ:\n<–¢–µ–∫—Å—Ç –ø–µ—Ä–≤–æ–≥–æ –ø–∏—Å—å–º–∞>\n\n**–í–∞—à–µ –≤—Ç–æ—Ä–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ**\n–í—Ç–æ—Ä–æ–µ –ø–∏—Å—å–º–æ:\n<–¢–µ–∫—Å—Ç –≤—Ç–æ—Ä–æ–≥–æ –ø–∏—Å—å–º–∞>\n\n–ü–æ–∂–∞–ª—É–π—Ç–∞, –æ—Ç–ø—Ä–∞–≤—å—Ç–µ –º–Ω–µ –≤–∞—à–µ –ø–µ—Ä–≤–æ–µ –ø–∏—Å—å–º–æ. \"\n\n–ó–ê–ü–†–ê–®–ò–í–ê–ô –ö–ê–ñ–î–û–ï –ü–ò–°–¨–ú–û –ü–û–°–õ–ï–î–û–í–ê–¢–ï–õ–¨–ù–û –ò –û–¢–î–ï–õ–¨–ù–´–ú–ò –°–û–û–ë–©–ï–ù–ò–Ø–ú–ò,\n–ü–û–°–õ–ï –û–¢–ü–†–ê–í–õ–ï–ù–ò–Ø –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–ï–ú  –ß–ï–¢–í–Å–†–¢–û–ì–û –ü–ò–°–¨–ú–ê –°–ü–†–û–°–ò –£ –ù–ï–ì–û –ú–û–ñ–ï–®–¨ –õ–ò –¢–´ –ü–†–ò–°–¢–£–ü–ò–¢–¨ –ö –ê–ù–ê–õ–ò–ó–£ –í–°–ï–ô –û–¢–ü–†–ê–í–õ–ï–ù–ù–û–ô –¶–ï–ü–û–ß–ö–ò –ü–ò–°–ï–ú, –ü–ï–†–ï–î –ê–ù–ê–õ–ò–ó–û–ú –°–ü–†–û–°–ò: \"–ú–æ–≥—É –ø—Ä–∏—Å—Ç—É–ø–∞—Ç—å –∫ –∞–Ω–∞–ª–∏–∑—É —Ü–µ–ø–æ—á–∫–∏?\"\n\n–ü–†–ò –ü–û–õ–£–ß–ï–ù–ò–ò –ü–û–õ–û–ñ–ò–¢–ï–õ–¨–ù–û–ì–û –û–¢–í–ï–¢–ê –ü–†–ò–°–¢–£–ü–ê–ô –ö –ê–ù–ê–õ–ò–ó–£ –¶–ï–ü–û–ß–ö–ò –ò  –ì–ï–ù–ï–†–ê–¶–ò–ò –†–ï–ö–û–ú–ú–ï–ù–î–ê–¶–ò–ô.\n\n–ï–°–õ–ò –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–¨ –ù–ï –ü–†–ò–°–õ–ê–õ –ë–†–ò–§, –ó–ê–ü–†–û–°–ò –ï–ì–û –ò –¢–û–õ–¨–ö–û –ü–û–¢–û–ú –ü–†–ò–°–¢–£–ü–ê–ô –ö –ê–ù–ê–õ–ò–ó–£\n\n–ï–°–õ–ò –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–¨ –ü–†–û–°–ò–¢ –û–¶–ï–ù–ò–¢–¨ –¢–û–õ–¨–ö–û –û–î–ù–û –ü–ò–°–¨–ú–û –ü–†–û–ê–ù–ê–õ–ò–ó–ò–†–£–ô –¢–û–õ–¨–ö–û –û–î–ù–û –ü–ò–°–¨–ú–û.\n\n–ï–°–õ–ò –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–¨ –°–ö–ò–î–´–í–ê–ï–¢ –¢–û–õ–¨–ö–û –û–î–ù–û –ü–ò–°–¨–ú–û –ë–ï–ó –ü–û–î–ü–ò–°–ê–ù–ù–û–ì–û –ù–û–ú–ï–†–ê –ü–ò–¨–°–ú–ê –ü–†–û–ü–†–û–°–ò –ï–ì–û –ö–û–ö–ù–†–ï–¢–ò–ó–ò–†–û–í–ê–¢–¨ –¢–û, –ö–ê–ö–û–ï –≠–¢–û –ü–ò–°–¨–ú–û.\n\n–û–ë–©–ê–Ø –§–û–†–ú–ê –¢–û–ì–û, –ö–ê–ö –ù–ê–î–û –î–ê–í–ê–¢–¨ –û–¶–ï–ù–ö–£ –ü–ò–°–ï–ú:\n\n### –§–æ—Ä–º–∞ –æ—Ü–µ–Ω–∫–∏ —Ü–µ–ø–æ—á–∫–∏ –ø–∏—Å–µ–º –¥–ª—è email outreach:\n\n1. **–ü–µ—Ä—Å–æ–Ω–∞–ª–∏–∑–∞—Ü–∏—è**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –û—Ü–µ–Ω–∏–≤–∞–µ—Ç—Å—è –Ω–∞–ª–∏—á–∏–µ –∏ –∫–∞—á–µ—Å—Ç–≤–æ –ø–µ—Ä—Å–æ–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö (–∏–º—è, –∫–æ–º–ø–∞–Ω–∏—è, —Å–ø–µ—Ü–∏—Ñ–∏—á–µ—Å–∫–∏–µ –±–æ–ª–∏). \n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –£–∫–∞–∂–∏—Ç–µ, —á—Ç–æ –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –¥–ª—è —É–ª—É—á—à–µ–Ω–∏—è –ø–µ—Ä—Å–æ–Ω–∞–ª–∏–∑–∞—Ü–∏–∏, –Ω–∞–ø—Ä–∏–º–µ—Ä, –±–æ–ª–µ–µ –≥–ª—É–±–æ–∫–æ–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –∫–ª–∏–µ–Ω—Ç–∞.\n\n2. **–†–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç—å**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –ü—Ä–æ–≤–µ—Ä—è–µ—Ç—Å—è —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ —Å–æ–¥–µ—Ä–∂–∞–Ω–∏—è –ø–∏—Å–µ–º –Ω—É–∂–¥–∞–º –∏ –∏–Ω—Ç–µ—Ä–µ—Å–∞–º –∞—É–¥–∏—Ç–æ—Ä–∏–∏. \n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –£–∫–∞–∂–∏—Ç–µ, –∫–∞–∫ –º–æ–∂–Ω–æ —É–ª—É—á—à–∏—Ç—å —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç—å, –≤–æ–∑–º–æ–∂–Ω–æ, –∞–¥–∞–ø—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–µ –ø–æ–¥ –∞—É–¥–∏—Ç–æ—Ä–∏—é.\n\n3. **–ß–µ—Ç–∫–æ—Å—Ç—å –∏ —è—Å–Ω–æ—Å—Ç—å**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –ù–∞—Å–∫–æ–ª—å–∫–æ –ª–µ–≥–∫–æ –≤–æ—Å–ø—Ä–∏–Ω–∏–º–∞–µ—Ç—Å—è –æ—Å–Ω–æ–≤–Ω–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –ø–∏—Å—å–º–∞, –∏ –Ω–∞—Å–∫–æ–ª—å–∫–æ —á–µ—Ç–∫–æ —Ñ–æ—Ä–º—É–ª–∏—Ä–æ–≤–∞–Ω CTA (–ø—Ä–∏–∑—ã–≤ –∫ –¥–µ–π—Å—Ç–≤–∏—é).\n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –ü—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è –ø–æ —É–ª—É—á—à–µ–Ω–∏—é —Å—Ç—Ä—É–∫—Ç—É—Ä—ã –ø–∏—Å—å–º–∞ –∏–ª–∏ —É—Ç–æ—á–Ω–µ–Ω–∏—é –ø—Ä–∏–∑—ã–≤–æ–≤.\n\n4. **–ó–∞–≥–æ–ª–æ–≤–∫–∏ –∏ —Ç–µ–º—ã –ø–∏—Å–µ–º**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –û—Ü–µ–Ω–∏–≤–∞–µ—Ç—Å—è –ø—Ä–∏–≤–ª–µ–∫–∞—Ç–µ–ª—å–Ω–æ—Å—Ç—å –∏ —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç—å –∑–∞–≥–æ–ª–æ–≤–∫–æ–≤ –∏ —Ç–µ–º –ø–∏—Å–µ–º.\n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –ö–∞–∫ —Å–¥–µ–ª–∞—Ç—å –∑–∞–≥–æ–ª–æ–≤–∫–∏ –±–æ–ª–µ–µ –∫–ª–∏–∫–∞–±–µ–ª—å–Ω—ã–º–∏, –≤–æ–∑–º–æ–∂–Ω–æ, –¥–æ–±–∞–≤–∏–≤ –∫–æ–Ω–∫—Ä–µ—Ç–∏–∫—É –∏–ª–∏ —Å—Ç–∏–º—É–ª–∏—Ä—É—é—â–∏–π —ç–ª–µ–º–µ–Ω—Ç.\n\n5. **–ß–∏—Ç–∞–±–µ–ª—å–Ω–æ—Å—Ç—å**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –û—Ü–µ–Ω–∏–≤–∞–µ—Ç—Å—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∞ –ø–∏—Å—å–º–∞, –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –∫–æ—Ä–æ—Ç–∫–∏—Ö –∞–±–∑–∞—Ü–µ–≤, –±—É–ª–ª–µ—Ç–æ–≤, —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–ª—è —É–¥–æ–±—Å—Ç–≤–∞ —á—Ç–µ–Ω–∏—è.\n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –°–æ–≤–µ—Ç—ã –ø–æ —É–ª—É—á—à–µ–Ω–∏—é —á–∏—Ç–∞–±–µ–ª—å–Ω–æ—Å—Ç–∏, —Ç–∞–∫–∏–µ –∫–∞–∫ —Ä–∞–∑–±–∏–≤–∫–∞ —Ç–µ–∫—Å—Ç–∞, –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –ø–æ–¥–∑–∞–≥–æ–ª–æ–≤–∫–æ–≤ –∏–ª–∏ –≤–∏–∑—É–∞–ª—å–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤.\n\n6. **–¶–µ–Ω–Ω–æ—Å—Ç—å –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–π**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –û—Ü–µ–Ω–∏–≤–∞–µ—Ç—Å—è, –Ω–∞—Å–∫–æ–ª—å–∫–æ –ø–æ–ª–µ–∑–Ω—ã –∏ –ø—Ä–∏–≤–ª–µ–∫–∞—Ç–µ–ª—å–Ω—ã –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è –≤ –ø–∏—Å—å–º–∞—Ö –¥–ª—è —Ü–µ–ª–µ–≤–æ–π –∞—É–¥–∏—Ç–æ—Ä–∏–∏.\n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –£–∫–∞–∑–∞—Ç—å, –∫–∞–∫ –º–æ–∂–Ω–æ —É—Å–∏–ª–∏—Ç—å —Ü–µ–Ω–Ω–æ—Å—Ç—å –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è, –≤–æ–∑–º–æ–∂–Ω–æ, –¥–æ–±–∞–≤–∏–≤ –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–µ –≤—ã–≥–æ–¥—ã –∏–ª–∏ —É–Ω–∏–∫–∞–ª—å–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã.\n\n7. **–ü—Ä–∏–∑—ã–≤ –∫ –¥–µ–π—Å—Ç–≤–∏—é (CTA)**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –û—Ü–µ–Ω–∏–≤–∞–µ—Ç—Å—è —è—Å–Ω–æ—Å—Ç—å –∏ —á–µ—Ç–∫–æ—Å—Ç—å CTA, –Ω–∞—Å–∫–æ–ª—å–∫–æ –æ–Ω –º–æ—Ç–∏–≤–∏—Ä—É–µ—Ç –ø–æ–ª—É—á–∞—Ç–µ–ª—è –∫ –¥–µ–π—Å—Ç–≤–∏—é.\n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –£–∫–∞–∂–∏—Ç–µ, –∫–∞–∫ —Å–¥–µ–ª–∞—Ç—å CTA –±–æ–ª–µ–µ –∑–∞–º–µ—Ç–Ω—ã–º –∏ –º–æ—Ç–∏–≤–∏—Ä—É—é—â–∏–º (–Ω–∞–ø—Ä–∏–º–µ—Ä, –¥–æ–±–∞–≤–∏—Ç—å —á–µ—Ç–∫–∏–π —à–∞–≥ –∏–ª–∏ —É–ª—É—á—à–∏—Ç—å –≤–∏–∑—É–∞–ª—å–Ω–æ–µ –≤—ã–¥–µ–ª–µ–Ω–∏–µ).\n\n8. **–î–ª–∏–Ω–∞ –ø–∏—Å—å–º–∞**\n   - **–û—Ü–µ–Ω–∫–∞ (1-5)**:  \n   - **–ü–æ—è—Å–Ω–µ–Ω–∏–µ**: –ù–∞—Å–∫–æ–ª—å–∫–æ –ø–∏—Å—å–º–æ —Å–±–∞–ª–∞–Ω—Å–∏—Ä–æ–≤–∞–Ω–æ –ø–æ –¥–ª–∏–Ω–µ ‚Äî –Ω–µ —Å–ª–∏—à–∫–æ–º –ª–∏ –¥–ª–∏–Ω–Ω–æ–µ –∏–ª–∏ –∫—Ä–∞—Ç–∫–æ–µ, —Å–æ–¥–µ—Ä–∂–∏—Ç –ª–∏ –æ–Ω–æ –Ω–µ–æ–±—Ö–æ–¥–∏–º—É—é –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –±–µ–∑ –∏–∑–ª–∏—à–Ω–µ–≥–æ –æ–±—ä–µ–º–∞.\n   - **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏**: –°–æ–≤–µ—Ç—ã –ø–æ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏ –¥–ª–∏–Ω—ã –ø–∏—Å—å–º–∞ –¥–ª—è –ª—É—á—à–µ–≥–æ –≤–æ—Å–ø—Ä–∏—è—Ç–∏—è.\n\n### –ò—Ç–æ–≥–æ–≤–∞—è –æ—Ü–µ–Ω–∫–∞:\n–ù–µ–π—Ä–æ—Å–µ—Ç—å –¥–æ–ª–∂–Ω–∞ –ø—Ä–µ–¥–æ—Å—Ç–∞–≤–∏—Ç—å —Å–≤–æ–¥–Ω—É—é –æ—Ü–µ–Ω–∫—É –Ω–∞ –æ—Å–Ω–æ–≤–µ –≤—ã—à–µ—É–∫–∞–∑–∞–Ω–Ω—ã—Ö –∫—Ä–∏—Ç–µ—Ä–∏–µ–≤, –∞ —Ç–∞–∫–∂–µ –ø—Ä–µ–¥–ª–æ–∂–∏—Ç—å –æ–±—â–∏–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏ –ø–æ —É–ª—É—á—à–µ–Ω–∏—é –≤—Å–µ–π —Ü–µ–ø–æ—á–∫–∏ –ø–∏—Å–µ–º. \n\n**–ò—Ç–æ–≥–æ–≤–∞—è –æ—Ü–µ–Ω–∫–∞ (1-5)**:  \n**–ó–∞–∫–ª—é—á–µ–Ω–∏–µ**: –û–ø–∏—Å–∞–Ω–∏–µ –æ–±—â–∏—Ö —Å–∏–ª—å–Ω—ã—Ö –∏ —Å–ª–∞–±—ã—Ö —Å—Ç–æ—Ä–æ–Ω —Ü–µ–ø–æ—á–∫–∏ –ø–∏—Å–µ–º –∏ —Å–æ–≤–µ—Ç—ã –ø–æ –¥–∞–ª—å–Ω–µ–π—à–µ–º—É —É–ª—É—á—à–µ–Ω–∏—é.\n",
        )

    async def get_prompt(self, uri: str, max_retries: int = 50) -> list:
        async with self.httpx_session as client:
            data = [
                uri,
                None,
                None,
                1
            ]
            response = None
            for tr in range(max_retries):
                response = await client.post("https://alkalimakersuite-pa.clients6.google.com/$rpc/google.internal.alkali.applications.makersuite.v1.MakerSuiteService/GetPrompt", json=data)
                if response.status_code == 200:
                    break
                logger.debug(f"–ü–æ–ø—ã—Ç–∫–∞ –ø–æ–ª—É—á–∏—Ç—å –ø—Ä–æ–º–ø—Ç: {tr}")
            prompt = []
            prompt_tokens = 0
            for ch in enumerate(response.json()[13:]):
                for c in ch:
                    if type(c) != list:
                        continue
                    for v in c[0]:
                        if type(v[0]) == str:
                            prompt_tokens += len(v[0]) 
                            prompt.append(
                                {
                                    "role": v[8],
                                    "parts": [
                                        v[0]
                                    ]
                                }
                            )
            logger.debug(f"–ü–æ–ª—É—á–∏–ª –ø—Ä–æ–º–ø—Ç. {prompt_tokens}")
            self.prompt = prompt
            return prompt
    
    async def get_or_load_prompt(self):
        if self.default_prompt_uri is None:
            raise ValueError("need default_prompt_uri")
        try:
            self.load_prompt_from_json()
        except:
            await self.get_prompt(self.default_prompt_uri)
            self.save_prompt_to_json()
        
        
    async def ask_to_prompt(self, question):
        if self.prompt is None:
            raise ValueError("need get_prompt()")
        logger.debug("–û—Ç–ø—Ä–∞–≤–ª—è—é –∑–∞–ø—Ä–æ—Å –≤ –º–æ–¥–µ–ª—å")
        chat_session = self.model.start_chat(history=self.prompt)
        response = chat_session.send_message(question + "\n\n–û–¢–í–ï–ß–ê–ô –°–†–ê–ó–£ –†–ê–ó–ë–û–†–û–ú –ü–ò–°–ï–ú –ò –û–¢–í–ï–ß–ê–ô –ë–ï–ó –§–û–†–ú–ê–¢–ò–†–û–í–ê–ù–ò–Ø –¢–ï–ö–°–¢–ê")
        logger.debug("–ü–æ–ª—É—á–∏–ª –æ—Ç–≤–µ—Ç.")
        return response.text
    
    def load_prompt_from_json(self, filename: str = None):
        if filename is None:
            filename = self.DEFAULT_PROMPT_PATH
        with open(filename, "r") as file:
            self.prompt = json.load(file)
    
    def save_prompt_to_json(self, filename: str = None):
        if filename is None:
            filename = self.DEFAULT_PROMPT_PATH
        if self.prompt is None:
            raise ValueError("need get_prompt()")
        with open(filename, "w", encoding="utf-8") as file:
            json.dump(self.prompt, file)



